  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"request"}}>{`Request`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="inlineCode" components={components} parentName="p">{`request`}</MDXTag>{` is wrapper of `}<MDXTag name="inlineCode" components={components} parentName="p">{`fetch`}</MDXTag>{` API, and  a brief call of Fetcher's `}<MDXTag name="inlineCode" components={components} parentName="p">{`fetchResource`}</MDXTag>{`. `}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"intro"}}>{`Intro`}</MDXTag>
<MDXTag name="p" components={components}>{`In `}<MDXTag name="inlineCode" components={components} parentName="p">{`react-restful`}</MDXTag>{`, `}<MDXTag name="inlineCode" components={components} parentName="p">{`request`}</MDXTag>{` is a common method to sending requests, usually used to send POST, PUT or DELETE. With GET, only use `}<MDXTag name="inlineCode" components={components} parentName="p">{`request`}</MDXTag>{` when you need to send multiple requests at a time. For example, we will send a POST request to create a Pet:`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"expample"}}>{`Expample`}</MDXTag>
<MDXTag name="p" components={components}>{`First, export your request from setup file:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`import { setupEnvironment } from 'react-restful';

const restfulEnv = setupEnvironment({
    // ...
});

export {
    request: restfulEnv.request
};
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Own`}<MDXTag name="inlineCode" components={components} parentName="p">{`pet`}</MDXTag>{` resources :`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-ts"}}>{`import { Resource, Record } from 'react-restful';

export interface Pet extends Record {
    id: number;
    name: string;
}

export const createPetResource = new Resource<Pet>({
    url: '/pet',
    method: 'POST'
});
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}><MDXTag name="inlineCode" components={components} parentName="p">{`request`}</MDXTag>{` returned a promise, so you can use async and await like this:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`async function createPet(pet: Pet) {
    const requestParam = { type: 'body', value: pet };
    try {
        const newPet = await request(
            createPetResource, 
            requestParam,
        );
        return newPet as Pet;
    } catch (error) {
        throw new Error(error);
    }
}

// somewhere in your app...
const newPet = await createPet({name: 'tom'});
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Type definition:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`type request = <DataModel, Meta = {}>(
    resource: Resource<DataModel>, 
    params?: RequestParameter | RequestParameter[], 
    meta?: Meta
) => Promise<DataModel>
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"about-meta-object"}}>{`About Meta object`}</MDXTag>
<MDXTag name="p" components={components}>{`Meta is optional, it can be anything you pass at the beginning of your request, and you can get it back at the end. For example, if you want to display a specific message to a user when a request completes (including success or failure), you need to put the message in the meta, and the message will be displayed at `}<MDXTag name="inlineCode" components={components} parentName="p">{`requestSuccess`}</MDXTag>{` (or `}<MDXTag name="inlineCode" components={components} parentName="p">{`requestFailed`}</MDXTag>{`) as the example above. My opinion here is that we should separate the business logic and the side-effect to make things clearer.`}</MDXTag>
           </MDXTag>
  }
}
